bool iscube(int a) {
    if (a < 0) {
        // If a is negative, we need to handle the cube root of a negative number
        a = -a;
        double cubeRoot = cbrt(a);
        int intCubeRoot = static_cast<int>(cubeRoot);
        return (intCubeRoot * intCubeRoot * intCubeRoot == a);
    } else {
        // If a is non-negative, we can directly take the cube root
        double cubeRoot = cbrt(a);
        int intCubeRoot = static_cast<int>(cubeRoot);
        return (intCubeRoot * intCubeRoot * intCubeRoot == a);
    }
}

