bool check_dict_case(map<string, string> dict) {
    // If the map is empty, return false
    if (dict.empty()) {
        return false;
    }

    bool all_lower = true;
    bool all_upper = true;

    for (const auto& pair : dict) {
        const string& key = pair.first;

        // Check if the key is a string
        if (key.empty()) {
            return false;
        }

        // Check if all characters in the key are either all lower case or all upper case
        bool is_lower = true;
        bool is_upper = true;

        for (char c : key) {
            if (!islower(c)) {
                is_lower = false;
            }
            if (!isupper(c)) {
                is_upper = false;
            }
        }

        // If the key is neither all lower case nor all upper case, return false
        if (!is_lower && !is_upper) {
            return false;
        }

        // Update the overall flags
        all_lower &= is_lower;
        all_upper &= is_upper;
    }

    // Return true if all keys are either all lower case or all upper case
    return all_lower || all_upper;
}

