string rounded_avg(int n, int m) {
    // Step 1: Check if n is greater than m
    if (n > m) {
        return "-1";
    }

    // Step 2: Calculate the average
    int sum = 0;
    for (int i = n; i <= m; ++i) {
        sum += i;
    }
    double average = static_cast<double>(sum) / (m - n + 1);

    // Step 3: Round the average to the nearest integer (smaller one)
    int rounded_average = floor(average);

    // Step 4: Convert the rounded average to binary
    string binary_representation;
    if (rounded_average == 0) {
        binary_representation = "0";
    } else {
        while (rounded_average > 0) {
            binary_representation = to_string(rounded_average % 2) + binary_representation;
            rounded_average /= 2;
        }
    }

    // Step 5: Return the binary string
    return binary_representation;
}

