    for (int num : n) {
        int sum_of_digits = 0;
        int temp = abs(num); // Work with the absolute value to handle digits

        // Extract digits and add them to the sum
        while (temp > 0) {
            int digit = temp % 10;
            if (num < 0) {
                digit = -digit; // Treat digits as signed if the number is negative
            }
            sum_of_digits += digit;
            temp /= 10;
        }

        // If the number is negative and has no positive digits, sum_of_digits will be negative
        if (sum_of_digits > 0) {
            count++;
        }
    }

    return count;
}

