    int n = arr.size();
    int min_index = 0;

    // Find the index of the minimum element
    for (int i = 1; i < n; ++i) {
        if (arr[i] < arr[min_index]) {
            min_index = i;
        }
    }

    // Check if the vector can be sorted by right shifts
    for (int i = 0; i < n - 1; ++i) {
        int current_index = (min_index + i) % n;
        int next_index = (min_index + i + 1) % n;

        if (arr[current_index] > arr[next_index]) {
            return false;
        }
    }

    return true;
}

