using namespace std;

long long minSubArraySum(vector<long long> nums) {
    // Step 1: Negate the elements
    for (int i = 0; i < nums.size(); ++i) {
        nums[i] = -nums[i];
    }

    // Step 2: Apply Kadane's algorithm to find the maximum sum subarray of the negated elements
    long long max_ending_here = nums[0];
    long long max_so_far = nums[0];

    for (int i = 1; i < nums.size(); ++i) {
        max_ending_here = max(nums[i], max_ending_here + nums[i]);
        max_so_far = max(max_so_far, max_ending_here);
    }

    // Step 3: Negate the result to get the minimum sum of the original elements
    return -max_so_far;
}

