vector<int> get_odd_collatz(int n) {
    vector<int> odd_numbers;

    // Generate the Collatz sequence and collect odd numbers
    while (n != 1) {
        if (n % 2 == 1) { // Check if n is odd
            odd_numbers.push_back(n);
        }
        if (n % 2 == 0) { // If n is even
            n = n / 2;
        } else { // If n is odd
            n = 3 * n + 1;
        }
    }
    // Add 1 to the list of odd numbers (since 1 is always the end of the sequence)
    odd_numbers.push_back(1);

    // Sort the vector of odd numbers
    sort(odd_numbers.begin(), odd_numbers.end());

    return odd_numbers;
}

