    ss >> month >> dash1 >> day >> dash2 >> year;

    // Check if the extracted values are valid
    if (dash1 != '-' || dash2 != '-' || ss.fail()) {
        return false;
    }

    // Validate the month
    if (month < 1 || month > 12) {
        return false;
    }

    // Validate the day based on the month
    if (month == 2) {
        if (day < 1 || day > 29) {
            return false;
        }
    } else if (month == 4 || month == 6 || month == 9 || month == 11) {
        if (day < 1 || day > 30) {
            return false;
        }
    } else {
        if (day < 1 || day > 31) {
            return false;
        }
    }

    // If all checks pass, the date is valid
    return true;
}

