    int start2 = interval2[0], end2 = interval2[1];

    int start_intersection = max(start1, start2);
    int end_intersection = min(end1, end2);

    if (start_intersection <= end_intersection) {
        int length_of_intersection = end_intersection - start_intersection + 1;
        if (isPrime(length_of_intersection)) {
            return "YES";
        }
    }

    return "NO";
}

