int prod_signs(vector<int> arr) {
    // Handle the special case for an empty vector
    if (arr.empty()) {
        return -32768;
    }

    int product_of_signs = 1;
    int sum_of_magnitudes = 0;

    for (int num : arr) {
        // Determine the sign of the number
        int sign = (num > 0) ? 1 : (num < 0) ? -1 : 0;
        // Update the product of signs
        product_of_signs *= sign;
        // Add the magnitude of the number to the sum
        sum_of_magnitudes += abs(num);
    }

    // Multiply the sum of magnitudes by the product of signs
    return sum_of_magnitudes * product_of_signs;
}

