#include <vector>
#include <queue>
#include <climits>
using namespace std;

struct State {
    vector<int> path;
    int x, y;
};

vector<int> minPath(vector<vector<int>> grid, int k) {
    int N = grid.size();
    vector<int> min_path(k, INT_MAX);

    queue<State> q;

    // Initialize the queue with all starting points
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            q.push({{grid[i][j]}, i, j});
        }
    }

    while (!q.empty()) {
        State current = q.front();
        q.pop();

        // If the current path length is k, compare and update min_path
        if (current.path.size() == k) {
            if (current.path < min_path) {
                min_path = current.path;
            }
            continue;
        }

        // Explore all 4 possible moves (up, down, left, right)
        int dx[] = {-1, 1, 0, 0};
        int dy[] = {0, 0, -1, 1};

        for (int d = 0; d < 4; ++d) {
            int nx = current.x + dx[d];
            int ny = current.y + dy[d];

            if (nx >= 0 && nx < N && ny >= 0 && ny < N) {
                vector<int> new_path = current.path;
                new_path.push_back(grid[nx][ny]);
                q.push({new_path, nx, ny});
            }
        }
    }

    return min_path;
}

