vector<int> tri(int n) {
    // Vector to store the Tribonacci sequence values
    vector<int> tribonacci(n + 1);

    // Base case
    tribonacci[0] = 1; // This is a placeholder for 0-based indexing
    tribonacci[1] = 3;

    // Fill the vector based on the given rules
    for (int i = 2; i <= n; ++i) {
        if (i % 2 == 0) {
            // Even case
            tribonacci[i] = 1 + i / 2;
        } else {
            // Odd case
            tribonacci[i] = tribonacci[i - 1] + tribonacci[i - 2] + tribonacci[i + 1];
        }
    }

    // Return the first n + 1 numbers
    return tribonacci;
}

