    int product = 1;

    // Flag to check if any odd digit is found
    bool oddFound = false;

    // Iterate through each character in the string
    for (char ch : numStr) {
        // Convert the character to an integer
        int digit = ch - '0';

        // Check if the digit is odd
        if (digit % 2 != 0) {
            // Multiply the product by the odd digit
            product *= digit;
            oddFound = true;
        }
    }

    // If no odd digit was found, return 0
    if (!oddFound) {
        return 0;
    }

    // Return the product of the odd digits
    return product;
}

