boost::any compare_one(boost::any a, boost::any b) {
    // Helper function to convert string to float
    auto to_float = [](const string& s) -> float {
        string temp = s;
        replace(temp.begin(), temp.end(), ',', '.');
        return boost::lexical_cast<float>(temp);
    };

    // Helper function to convert any type to float
    auto any_to_float = [&](const boost::any& val) -> float {
        if (val.type() == typeid(int)) {
            return static_cast<float>(boost::any_cast<int>(val));
        } else if (val.type() == typeid(float)) {
            return boost::any_cast<float>(val);
        } else if (val.type() == typeid(string)) {
            return to_float(boost::any_cast<string>(val));
        }
        throw runtime_error("Unsupported type");
    };

    // Convert both values to float for comparison
    float a_float = any_to_float(a);
    float b_float = any_to_float(b);

    // Compare the values
    if (a_float > b_float) {
        return a;
    } else if (b_float > a_float) {
        return b;
    } else {
        return string("None");
    }
}

