bool simplify(string x, string n) {
    // Helper function to parse the fraction string
    auto parseFraction = [](const string& fraction) -> pair<int, int> {
        size_t pos = fraction.find('/');
        int numerator = stoi(fraction.substr(0, pos));
        int denominator = stoi(fraction.substr(pos + 1));
        return {numerator, denominator};
    };

    // Parse the fractions
    auto [xNumerator, xDenominator] = parseFraction(x);
    auto [nNumerator, nDenominator] = parseFraction(n);

    // Multiply the fractions
    int resultNumerator = xNumerator * nNumerator;
    int resultDenominator = xDenominator * nDenominator;

    // Check if the result is a whole number
    return resultNumerator % resultDenominator == 0;
}

