// Helper function to calculate the sum of digits of a number
int sum_of_digits(int num) {
    int sum = 0;
    bool is_negative = num < 0;
    num = abs(num);

    while (num > 0) {
        int digit = num % 10;
        if (is_negative) {
            digit = -digit;
            is_negative = false; // Only the first digit should be negative
        }
        sum += digit;
        num /= 10;
    }

    return sum;
}

vector<int> order_by_points(vector<int> nums) {
    // Create a vector of pairs where each pair contains the original number and its digit sum
    vector<pair<int, int>> num_sum_pairs;
    for (int num : nums) {
        num_sum_pairs.push_back({num, sum_of_digits(num)});
    }

    // Sort the vector of pairs based on the digit sum
    sort(num_sum_pairs.begin(), num_sum_pairs.end(), [](const pair<int, int>& a, const pair<int, int>& b) {
        if (a.second == b.second) {
            // If digit sums are equal, maintain the original order
            return false;
        }
        return a.second < b.second;
    });

    // Extract the sorted numbers from the sorted pairs
    vector<int> sorted_nums;
    for (const auto& pair : num_sum_pairs) {
        sorted_nums.push_back(pair.first);
    }

    return sorted_nums;
}

