            odd_count++; // Increment odd count if the digit is odd
        }
        num /= 10; // Remove the last digit from the number
    }

    // Return the counts as a vector
    return {even_count, odd_count};
}

#include <iostream>
#include <vector>
#include <string>
using namespace std;

string int_to_mini_roman(int number) {
    // Define the Roman numeral symbols and their values in descending order
    vector<pair<int, string>> roman_symbols = {
        {1000, "m"}, {900, "cm"}, {500, "d"}, {400, "cd"},
        {100, "c"}, {90, "xc"}, {50, "l"}, {40, "xl"},
        {10, "x"}, {9, "ix"}, {5, "v"}, {4, "iv"}, {1, "i"}
    };

    string result = "";

    // Iterate through the symbols
    for (const auto& symbol : roman_symbols) {
        while (number >= symbol.first) {
            result += symbol.second;
            number -= symbol.first;
        }
    }

    return result;
}

#include<stdio.h>
#include<math.h>

bool right_angle_triangle(float a, float b, float c) {
    // Ensure the sides are in non-decreasing order
    if (a > b) {
        float temp = a;
        a = b;
        b = temp;
    }
    if (b > c) {
        float temp = b;
        b = c;
        c = temp;
    }
    if (a > b) {
        float temp = a;
        a = b;
        b = temp;
    }

    // Now c is the longest side
    return (pow(a, 2) + pow(b, 2) == pow(c, 2));
}

#include <iostream>
#include <vector>
#include <string>
#include <unordered_set>
#include <algorithm>

using namespace std;

string find_max(vector<string> words) {
    string max_word = "";
    int max_unique_count = 0;

    for (const string& word : words) {
        // Use an unordered_set to count unique characters
        unordered_set<char> unique_chars(word.begin(), word.end());
        int unique_count = unique_chars.size();

        // If the current word has more unique characters, update max_word
        if (unique_count > max_unique_count) {
            max_word = word;
            max_unique_count = unique_count;
        }
        // If the current word has the same number of unique characters but is lexicographically smaller, update max_word
        else if (unique_count == max_unique_count && word < max_word) {
            max_word = word;
        }
    }

    return max_word;
}

#include<stdio.h>
#include<vector>
using namespace std;

vector<int> eat(int number, int need, int remaining) {
    // Calculate the total number of carrots the rabbit needs to eat
    int total_needed = number + need;

    // Determine how many carrots the rabbit can actually eat
    int actually_eaten = min(total_needed, remaining);

    // Calculate the number of carrots left after the rabbit has eaten
    int carrots_left = remaining - actually_eaten;

    // Return the results as a vector
    return {number + actually_eaten, carrots_left};
}

