#include <iostream>
#include <vector>
#include <string>
using namespace std;

int do_algebra(vector<string> operato, vector<int> operand) {
    // Initialize the result with the first operand
    int result = operand[0];

    // Iterate through the operators and operands
    for (size_t i = 0; i < operato.size(); ++i) {
        // Get the current operator and operand
        string op = operato[i];
        int next_operand = operand[i + 1];

        // Apply the operator to the result and the next operand
        if (op == "+") {
            result += next_operand;
        } else if (op == "-") {
            result -= next_operand;
        } else if (op == "*") {
            result *= next_operand;
        } else if (op == "//") {
            result /= next_operand; // Note: This is integer division
        } else if (op == "**") {
            result = pow(result, next_operand);
        }
    }

    // Return the final result
    return result;
}

#include <iostream>
#include <string>
#include <cctype>
using namespace std;

string solve(string s) {
    bool containsLetter = false;
    for (char &c : s) {
        if (isalpha(c)) {
            containsLetter = true;
            if (islower(c)) {
                c = toupper(c);
            } else {
                c = tolower(c);
            }
        }
    }

    // If the string contains no letters, reverse the string
    if (!containsLetter) {
        reverse(s.begin(), s.end());
    }

    return s;
}

#include <stdio.h>
#include <string>
#include <openssl/md5.h>
#include <iomanip>
#include <sstream>

using namespace std;

string string_to_md5(const string& text) {
    // If the input string is empty, return None
    if (text.empty()) {
        return "";
    }

    // Create an MD5 context
    MD5_CTX md5Context;
    MD5_Init(&md5Context);

    // Update the context with the input string
    MD5_Update(&md5Context, text.c_str(), text.length());

    // Create a buffer to hold the MD5 hash
    unsigned char md5Digest[MD5_DIGEST_LENGTH];
    MD5_Final(md5Digest, &md5Context);

    // Convert the MD5 hash to a hexadecimal string
    stringstream ss;
    for (int i = 0; i < MD5_DIGEST_LENGTH; ++i) {
        ss << hex << setw(2) << setfill('0') << (int)md5Digest[i];
    }

    return ss.str();
}

#include<stdio.h>
#include<vector>
#include<algorithm>
using namespace std;

vector<int> generate_integers(int a, int b) {
    // Ensure a is less than or equal to b
    if (a > b) {
        swap(a, b);
    }

    vector<int> even_digits;

    // Even digits less than 10 are 2, 4, 6, and 8
    for (int i = a; i <= b; ++i) {
        if (i % 2 == 0 && i < 10) {
            even_digits.push_back(i);
        }
    }

    // Sort the even digits in ascending order (though they should already be sorted)
    sort(even_digits.begin(), even_digits.end());

    return even_digits;
}
