using namespace std;

// Function to remove spaces from the input string
string remove_spaces(string input_string) {
    string space_free_string = "";
    for (char c : input_string) {
        if (c != ' ') {
            space_free_string += c;
        }
    }
    return space_free_string;
}

// Function to identify individual parenthesis groups
vector<string> identify_paren_groups(string space_free_string) {
    vector<string> paren_groups;
    string current_group = "";
    int balance = 0;
    for (char c : space_free_string) {
        current_group += c;
        if (c == '(') {
            balance++;
        } else if (c == ')') {
            balance--;
        }
        if (balance == 0) {
            paren_groups.push_back(current_group);
            current_group = "";
        }
    }
    return paren_groups;
}

// Function to extract and separate parenthesis groups
vector<string> extract_paren_groups(vector<string> paren_groups) {
    return paren_groups;
}

// Main function to separate parenthesis groups
vector<string> separate_paren_groups(string paren_string) {
    string space_free_string = remove_spaces(paren_string);
    vector<string> paren_groups = identify_paren_groups(space_free_string);
    vector<string> extracted_groups = extract_paren_groups(paren_groups);
    return extracted_groups;
}

