using namespace std;

string string_xor(string a, string b) {
    // Input Validation
    if (a.length() != b.length()) {
        throw invalid_argument("Input strings must have the same length.");
    }

    for (char c : a) {
        if (c != '0' && c != '1') {
            throw invalid_argument("Input strings must consist only of 1s and 0s.");
        }
    }

    for (char c : b) {
        if (c != '0' && c != '1') {
            throw invalid_argument("Input strings must consist only of 1s and 0s.");
        }
    }

    // Binary XOR Operation
    string result;
    for (int i = 0; i < a.length(); i++) {
        result += (a[i] == b[i]) ? '0' : '1';
    }

    return result;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (string_xor("111000", "101010") == "010010");
     assert (string_xor("1", "1") == "0");
    assert (string_xor("0101", "0000") == "0101");

}

