std::pair<int, int> sum_product(const std::vector<int>& numbers) {
    int sum = std::accumulate(numbers.begin(), numbers.end(), 0);
    int product = 1;
    for (const auto& num : numbers) {
        product *= num;
    }
    return std::make_pair(sum, product);
}

