#include <stdio.h>
#include <string>
using namespace std;

bool is_palindrome(string str) {
    // Test if given string is a palindrome 
    string s(str.rbegin(), str.rend());
    return s == str;
}

string make_palindrome(string str) {
    if (is_palindrome(str)) {
        return str;
    }

    string longest_palindromic_suffix = "";
    for (int i = str.length() - 1; i >= 0; --i) {
        string suffix = str.substr(i);
        if (is_palindrome(suffix)) {
            longest_palindromic_suffix = suffix;
            break;
        }
    }

    string non_palindromic_prefix = str.substr(0, str.length() - longest_palindromic_suffix.length());
    string reversed_prefix(non_palindromic_prefix.rbegin(), non_palindromic_prefix.rend());

    return str + reversed_prefix;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (make_palindrome("") == "");
    assert (make_palindrome("x") == "x");
     assert (make_palindrome("xyz") == "xyzyx");
     assert (make_palindrome("xyx") == "xyx") ;
     assert (make_palindrome("jerry") == "jerryrrej");
}

