#include <stdio.h>
#include <vector>
#include <string>
#include <stack>
#include <sstream>

using namespace std;

vector<int> parse_nested_parens(string paren_string) {
    vector<int> result;
    istringstream iss(paren_string);
    string group;

    while (iss >> group) {
        int max_level = 0;
        int current_level = 0;
        stack<char> s;

        for (char c : group) {
            if (c == '(') {
                s.push(c);
                current_level++;
                max_level = max(max_level, current_level);
            } else if (c == ')') {
                if (s.empty()) {
                    // Invalid input: closing parenthesis without matching opening parenthesis
                    throw invalid_argument("Invalid input string");
                }
                s.pop();
                current_level--;
            }
        }

        if (!s.empty()) {
            // Invalid input: opening parentheses without matching closing parentheses
            throw invalid_argument("Invalid input string");
        }

        result.push_back(max_level);
    }

    return result;
}

