string longest(const std::vector<std::string>& strings) {
    if (strings.empty()) {
        throw std::invalid_argument("Input vector is empty");
    }

    size_t max_length = 0;
    std::string longest_string;

    for (const auto& str : strings) {
        if (str.size() > max_length) {
            max_length = str.size();
            longest_string = str;
        }
    }

    return longest_string;
}

