using namespace std;

int how_many_times(string str, string substring) {
    if (str.empty() || substring.empty()) {
        return 0;
    }

    int match_count = 0;
    int window_size = substring.length();

    for (int i = 0; i <= str.length() - window_size; i++) {
        if (str.substr(i, window_size) == substring) {
            match_count++;
        }
    }

    return match_count;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (how_many_times("", "x") == 0);
    assert (how_many_times("xyxyxyx", "x") == 4);
     assert (how_many_times("cacacacac", "cac") == 4);
    assert (how_many_times("john doe", "john") == 1);
}

