#include <algorithm>
#include <vector>

vector<float> rescale_to_unit(vector<float> numbers) {
    if (numbers.size() < 2) {
        throw std::invalid_argument("Input vector must have at least two elements");
    }

    float min_value = *std::min_element(numbers.begin(), numbers.end());
    float max_value = *std::max_element(numbers.begin(), numbers.end());
    float range = max_value - min_value;

    vector<float> transformed_numbers;
    for (float num : numbers) {
        transformed_numbers.push_back((num - min_value) / range);
    }

    return transformed_numbers;
}

