using namespace std;

vector<int> parse_music(string music_string) {
    vector<int> beat_durations;
    string note;
    for (char c : music_string) {
        if (c == ' ') {
            if (note == "o") beat_durations.push_back(4);
            else if (note == "o|") beat_durations.push_back(2);
            else if (note == ".|") beat_durations.push_back(1);
            note.clear();
        } else {
            note += c;
        }
    }
    // Handle the last note
    if (note == "o") beat_durations.push_back(4);
    else if (note == "o|") beat_durations.push_back(2);
    else if (note == ".|") beat_durations.push_back(1);
    return beat_durations;
}

