using namespace std;

string sort_numbers(string numbers) {
    map<string, int> numeral_map = {
        {"zero", 0}, {"one", 1}, {"two", 2}, {"three", 3}, {"four", 4},
        {"five", 5}, {"six", 6}, {"seven", 7}, {"eight", 8}, {"nine", 9}
    };

    vector<string> tokenized_numbers;
    string temp = "";
    for (char c : numbers) {
        if (c == ' ') {
            tokenized_numbers.push_back(temp);
            temp = "";
        } else {
            temp += c;
        }
    }
    tokenized_numbers.push_back(temp);

    vector<int> numerical_values;
    for (string numeral : tokenized_numbers) {
        numerical_values.push_back(numeral_map[numeral]);
    }

    sort(numerical_values.begin(), numerical_values.end());

    vector<string> sorted_numerals;
    for (int value : numerical_values) {
        for (auto& pair : numeral_map) {
            if (pair.second == value) {
                sorted_numerals.push_back(pair.first);
                break;
            }
        }
    }

    string result = "";
    for (string numeral : sorted_numerals) {
        result += numeral + " ";
    }
    result.pop_back(); // remove the trailing space

    return result;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (sort_numbers("") == "");
    assert (sort_numbers("three") == "three");
    assert (sort_numbers("three five nine") == "three five nine");
     assert (sort_numbers("five zero four seven nine eight") == "zero four five seven eight nine");
      assert (sort_numbers("six five four three two one zero") == "zero one two three four five six");
}

