#include <vector>
#include <map>
#include <algorithm>
using namespace std;

vector<int> factorize(int n) {
    if (n <= 0) {
        throw invalid_argument("Input must be a positive integer.");
    }

    vector<int> primeFactors;
    map<int, int> multiplicity;

    for (int i = 2; i * i <= n; ++i) {
        while (n % i == 0) {
            primeFactors.push_back(i);
            n /= i;
        }
    }

    if (n > 1) {
        primeFactors.push_back(n);
    }

    for (const auto& factor : primeFactors) {
        ++multiplicity[factor];
    }

    vector<int> result;
    for (const auto& pair : multiplicity) {
        for (int i = 0; i < pair.second; ++i) {
            result.push_back(pair.first);
        }
    }

    sort(result.begin(), result.end());
    return result;
}

