vector<int> sort_third(vector<int> l) {
    vector<int> sorted_values;
    for (int i = 0; i < l.size(); i++) {
        if (i % 3 == 0) {
            sorted_values.push_back(l[i]);
        }
    }
    sort(sorted_values.begin(), sorted_values.end());
    int j = 0;
    for (int i = 0; i < l.size(); i++) {
        if (i % 3 == 0) {
            l[i] = sorted_values[j++];
        }
    }
    return l;
}

