double poly(const std::vector<double>& xs, double x) {
    double sum = 0.0;
    for (size_t i = 0; i < xs.size(); ++i) {
        sum += xs[i] * pow(x, i);
    }
    return sum;
}

double find_zero(const std::vector<double>& xs) {
    if (xs.size() % 2 != 0) {
        throw std::invalid_argument("Number of coefficients must be even.");
    }

    double max_coeff = 0.0;
    for (double coeff : xs) {
        if (std::abs(coeff) > std::abs(max_coeff)) {
            max_coeff = coeff;
        }
    }

    if (max_coeff == 0.0) {
        throw std::invalid_argument("Largest coefficient must be non-zero.");
    }

    double x = 1.0; // initial guess
    double tolerance = 1e-6;
    double max_iterations = 1000;

    for (int i = 0; i < max_iterations; ++i) {
        double f_x = poly(xs, x);
        double f_prime_x = 0.0;
        for (size_t j = 1; j < xs.size(); ++j) {
            f_prime_x += j * xs[j] * pow(x, j - 1);
        }

        if (std::abs(f_prime_x) < tolerance) {
            throw std::runtime_error("Derivative is zero.");
        }

        double x_new = x - f_x / f_prime_x;
        if (std::abs(x_new - x) < tolerance) {
            return x_new;
        }

        x = x_new;
    }

    throw std::runtime_error("Failed to converge.");
}

#undef NDEBUG
#include<assert.h>
int main(){

    double solution;
    int ncoeff;
    for (int i=0;i<100;i++)
    {
        ncoeff = 2 * (1+rand()%4);
        vector<double> coeffs = {};
        for (int j=0;j<ncoeff;j++)
        {
            double coeff = -10+rand()%21;
            if (coeff == 0) coeff = 1;
            coeffs.push_back(coeff);
        }

        solution = find_zero(coeffs);
        assert (abs(poly(coeffs, solution))< 1e-3);
    }
}

