bool is_prime(int num) {
    if (num <= 1) return false;
    for (int i = 2; i <= sqrt(num); i++) {
        if (num % i == 0) return false;
    }
    return true;
}

int prime_fib(int n) {
    int count = 0;
    int a = 0, b = 1;
    while (true) {
        int fib = a;
        a = b;
        b = fib + b;
        if (is_prime(fib) && ++count == n) return fib;
    }
}


