string change_base(int x, int base) {
    if (x < 0 || base < 2 || base > 9) {
        throw invalid_argument("Invalid input: x must be non-negative and base must be between 2 and 9");
    }

    if (x == 0) {
        return "0";
    }

    string result = "";
    while (x > 0) {
        int remainder = x % base;
        char digit = '0' + remainder;
        result = digit + result;
        x /= base;
    }

    return result;
}

