int fib4(int n) {
    if (n < 0) {
        printf("Error: n must be a non-negative integer.\n");
        return -1; // Return an error value
    }

    if (n <= 3) {
        // Handle base cases
        if (n == 0 || n == 1 || n == 3) {
            return 0;
        } else if (n == 2) {
            return 2;
        }
    }

    int fib4_values[4] = {0, 0, 2, 0}; // Initialize the first four elements
    int i;
    for (i = 4; i <= n; i++) {
        // Calculate the next element as the sum of the previous four elements
        int next_fib4 = fib4_values[0] + fib4_values[1] + fib4_values[2] + fib4_values[3];
        // Shift the array to make room for the new element
        fib4_values[0] = fib4_values[1];
        fib4_values[1] = fib4_values[2];
        fib4_values[2] = fib4_values[3];
        fib4_values[3] = next_fib4;
    }

    // Return the calculated n-th element
    return fib4_values[3];
}

