int modp(int n, int p) {
    if (p <= 1) {
        throw invalid_argument("p must be greater than 1");
    }

    if (n < 0) {
        n = n % (p - 1);
        if (n < 0) {
            n += p - 1;
        }
    }

    unsigned long long result = 1;
    unsigned long long base = 2;

    while (n > 0) {
        if (n % 2 == 1) {
            result = (result * base) % p;
        }
        base = (base * base) % p;
        n /= 2;
    }

    return result;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (modp(3, 5) == 3);
    assert (modp(1101, 101) == 2);
    assert (modp(0, 101) == 1);
    assert (modp(3, 11) == 8);
    assert (modp(100, 101) == 1);
    assert (modp(30, 5) == 4);
    assert (modp(31, 5) == 3);
}

