/*
Return median of elements in the vector l.
>>> median({3, 1, 2, 4, 5})
3
>>> median({-10, 4, 6, 1000, 10, 20})
8.0
*/
#include<stdio.h>
#include<math.h>
#include<vector>
#include<algorithm>
using namespace std;

float median(vector<float> l) {
    if (l.empty()) {
        // Handle empty input vector
        return NAN;
    }

    // Sort the vector in ascending order
    std::sort(l.begin(), l.end());

    size_t n = l.size();
    if (n % 2 == 1) {
        // If the vector has an odd number of elements, return the middle element
        return l[n / 2];
    } else {
        // If the vector has an even number of elements, return the average of the two middle elements
        return (l[n / 2 - 1] + l[n / 2]) / 2.0f;
    }
}

