#include <vector>
#include <limits>

bool below_threshold(const std::vector<int>& l, int t) {
  if (l.empty()) return true; // input validation
  if (t != t || t == std::numeric_limits<int>::infinity() || t == -std::numeric_limits<int>::infinity()) {
    throw std::invalid_argument("Threshold is not a valid integer");
  }
  for (const int& x : l) { // vector iteration
    if (x >= t) { // comparison
      return false;
    }
  }
  return true;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (below_threshold({1, 2, 4, 10}, 100));
    assert (not(below_threshold({1, 20, 4, 10}, 5)));
    assert (below_threshold({1, 20, 4, 10}, 21));
    assert (below_threshold({1, 20, 4, 10}, 22));
    assert (below_threshold({1, 8, 4, 10}, 11));
    assert (not(below_threshold({1, 8, 4, 10}, 10)));
}

