using namespace std;

bool same_chars(string s0, string s1) {
    // Preprocessing: Convert both input strings to lowercase
    transform(s0.begin(), s0.end(), s0.begin(), ::tolower);
    transform(s1.begin(), s1.end(), s1.begin(), ::tolower);

    // Character Set Extraction: Use a set to store unique characters from each input string
    set<char> set0(s0.begin(), s0.end());
    set<char> set1(s1.begin(), s1.end());

    // Character Set Comparison: Compare the two extracted character sets for equality
    return set0 == set1;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (same_chars("eabcdzzzz", "dddzzzzzzzddeddabc") == true);
    assert (same_chars("abcd", "dddddddabc") == true);
    assert (same_chars("dddddddabc", "abcd") == true);
    assert (same_chars("eabcd", "dddddddabc") == false);
    assert (same_chars("abcd", "dddddddabcf") == false);
    assert (same_chars("eabcdzzzz", "dddzzzzzzzddddabc") == false);
    assert (same_chars("aabb", "aaccc") == false);
}

