#include <stdio.h>

int fib(int n) {
    if (n <= 0) {
        printf("Error: Input should be a positive integer.\n");
        return -1; // Return an error code
    }

    if (n == 1) {
        return 1;
    }

    int prev = 1;
    int curr = 1;
    int result = 0;

    for (int i = 2; i <= n; i++) {
        result = prev + curr;
        prev = curr;
        curr = result;
    }

    return result;
}


#undef NDEBUG
#include<assert.h>
