int fibfib(int n) {
    if (n <= 2) {
        if (n == 0 || n == 1) return 0;
        else return 1;
    }

    std::vector<int> fibfib_values(n + 1);
    fibfib_values[0] = 0;
    fibfib_values[1] = 0;
    fibfib_values[2] = 1;

    for (int i = 3; i <= n; i++) {
        fibfib_values[i] = fibfib_values[i - 1] + fibfib_values[i - 2] + fibfib_values[i - 3];
    }

    return fibfib_values[n];
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (fibfib(2) == 1);
    assert (fibfib(1) == 0);
    assert (fibfib(5) == 4);
    assert (fibfib(8) == 24);
    assert (fibfib(10) == 81);
    assert (fibfib(12) == 274);
    assert (fibfib(14) == 927);
}

