    if (s.empty()) return vowel_count;

    for (char& c : s) {
        c = tolower(c);
        switch (c) {
            case 'a':
            case 'e':
            case 'i':
            case 'o':
            case 'u':
                vowel_count++;
                break;
        }
    }

    if (!s.empty() && s.back() == 'y') vowel_count++;

    return vowel_count;
}

