#include <string>
#include <stdexcept>

string circular_shift(int x, int shift) {
    // Input validation
    if (x < 0 || shift < 0) {
        throw std::invalid_argument("Input values must be non-negative.");
    }

    // Convert integer to string
    std::string str_x = std::to_string(x);

    // Calculate effective shift
    int num_digits = str_x.length();
    int effective_shift = shift % num_digits;

    // Handle reverse case
    if (effective_shift == 0 && shift > num_digits) {
        // Reverse the string
        std::string reversed_str_x(str_x.rbegin(), str_x.rend());
        return reversed_str_x;
    }

    // Perform circular shift
    std::string shifted_str_x = str_x.substr(num_digits - effective_shift) + str_x.substr(0, num_digits - effective_shift);
    return shifted_str_x;
}

#undef NDEBUG
#include<assert.h>
int main(){
    assert (circular_shift(100, 2) == "001");
    assert (circular_shift(12, 2) == "12");
    assert (circular_shift(97, 8) == "79");
    assert (circular_shift(12, 1) == "21");
    assert (circular_shift(11, 101) == "11");
}

