float triangle_area(float a, float b, float c) {
    // Input Validation
    if (a <= 0 || b <= 0 || c <= 0) {
        return -1;
    }

    // Validate Triangle
    if (a + b <= c || a + c <= b || b + c <= a) {
        return -1;
    }

    // Calculate Semi-Perimeter
    float s = (a + b + c) / 2;

    // Calculate Area
    float A = sqrt(s * (s - a) * (s - b) * (s - c));

    // Round Area
    A = round(A * 100) / 100;

    // Return Result
    return A;
}

