vector<int> strange_sort_list(vector<int> lst) {
    vector<int> result;
    vector<int> copy = lst;

    while (!copy.empty()) {
        auto min_iter = min_element(copy.begin(), copy.end());
        result.push_back(*min_iter);
        copy.erase(min_iter);

        if (!copy.empty()) {
            auto max_iter = max_element(copy.begin(), copy.end());
            result.push_back(*max_iter);
            copy.erase(max_iter);
        }
    }

    return result;
}

