using namespace std;

bool will_it_fly(vector<int> q, int w) {
  // Input validation
  if (q.empty() || w < 0) {
    return false;
  }

  // Check palindromic property
  vector<int> reversed_q = q;
  reverse(reversed_q.begin(), reversed_q.end());
  if (q != reversed_q) {
    return false;
  }

  // Calculate sum of vector elements
  int sum = 0;
  for (int elem : q) {
    sum += elem;
  }

  // Compare sum with maximum weight
  if (sum > w) {
    return false;
  }

  // Return result
  return true;
}

