using namespace std;

int search(vector<int> lst) {
    // Input Validation
    if (lst.empty()) {
        return -1;
    }

    // Count Frequency
    map<int, int> frequency;
    for (int num : lst) {
        frequency[num]++;
    }

    // Filter Integers
    vector<int> filtered;
    for (auto& pair : frequency) {
        if (pair.second >= pair.first) {
            filtered.push_back(pair.first);
        }
    }

    // Find Greatest Integer
    if (filtered.empty()) {
        return -1;
    }
    return *max_element(filtered.begin(), filtered.end());
}

