    if (decimal_value <= 1) return false;
    for (int i = 2; i * i <= decimal_value; i++) {
        if (decimal_value % i == 0) return false;
    }
    return true;
}

int hex_key(string num) {
    int prime_count = 0;
    for (char c : num) {
        int decimal_value;
        if (c >= '0' && c <= '9') {
            decimal_value = c - '0';
        } else {
            decimal_value = c - 'A' + 10;
        }
        if (is_prime(decimal_value)) prime_count++;
    }
    return prime_count;
}

