int smallest_change(vector<int> arr) {
    int n = arr.size();

    // Check if the input vector is empty or has an odd length
    if (n == 0 || (n % 2 != 0)) {
        return -1; // Return an error code
    }

    int changes = 0;
    int left = 0;
    int right = n - 1;

    // Compare the input vector with its reverse
    while (left < right) {
        if (arr[left] != arr[right]) {
            changes++;
        }
        left++;
        right--;
    }

    return changes;
}

