bool is_simple_power(int x, int n) {
  if (x < 0 || n < 0) { // edge case: negative inputs
    return false;
  }
  if (x == 0 || n == 0) { // edge case: zero inputs
    return false;
  }
  if (x == 1) { // edge case: x is 1
    return true;
  }
  double p = std::log(x) / std::log(n); // calculate the power
  if (std::floor(p) == p) { // check if the power is an integer
    return true;
  }
  return false;
}


