string decimal_to_binary(int decimal) {
    if (decimal < 0) {
        return "Error: Input must be a non-negative integer.";
    }

    string binary = "db";
    while (decimal > 0) {
        binary += (decimal % 2 == 0) ? '0' : '1';
        decimal /= 2;
    }
    binary += "db";

    return binary;
}

